package com.yuqiaodan.mydemo.utils

import android.content.pm.ApplicationInfo
import android.text.TextUtils
import android.util.Base64
import android.util.Log
import com.google.gson.Gson
import com.yuqiaodan.mydemo.base.App
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.GlobalScope
import kotlinx.coroutines.launch

/**
 * Created by qiaodan on 2021/9/14
 * Description:
 */
object AppListUtils {

    val TAG = "AppListUtils"

    //排除的app
    val excludeApp = arrayListOf<String>()


    val vivoAppList =
        ""
    val oppoAppList = ""

    private var isInitEx = false

    fun getQdAppList(action: (List<DevApp>) -> Unit) {
        var exNum = 0
        GlobalScope.launch(Dispatchers.IO) {
            initExcludeApp()
            val pm = App.context.packageManager
            val list = pm.getInstalledPackages(0)
            val appList = arrayListOf<DevApp>()
            for (appInfo in list) {
                val appName = appInfo.applicationInfo.loadLabel(pm)
                val pkgName = appInfo.packageName
                var isSystem = false
                if (appInfo.applicationInfo.flags and ApplicationInfo.FLAG_SYSTEM != 0) {
                    isSystem = true
                }
                if (TextUtils.isEmpty(pkgName)) {
                    //排除空包名
                    continue
                }
                if (appInfo.packageName == App.instance.packageName) {
                    //排除本应用
                    continue
                }
                if (!excludeApp.contains(pkgName)) {
                    appList.add(DevApp(appName.toString(), pkgName, isSystem))
                } else {
                    exNum += 1
                }
            }
            Log.d(TAG, "扫描完毕 本次排除应用 $exNum 个")
            action.invoke(appList)
        }
    }


    fun initExcludeApp() {
        if (isInitEx) {
            Log.d(TAG, "initExcludeApp:  excludeApp已经初始化  ")
            return
        }
        isInitEx = true
        val json = String(Base64.decode(vivoAppList, Base64.NO_WRAP))
        val bean = Gson().fromJson(json, ExApp::class.java)
        excludeApp.addAll(bean.app_list)
        Log.d(TAG, "initExcludeApp:excludeApp长度${excludeApp.size}")
    }


    data class DevApp(val app_name: String?, val package_name: String?, val is_system: Boolean?)


    data class ExApp(val app_list: List<String>)
}

